mapper.insert:
insert into subsection (subsectionId, subsectionName, subsectionTime, 
  subsectionChecked, section_sectionId, subsectionContent
  )
values (#{subsectionId,jdbcType=INTEGER}, #{subsectionName,jdbcType=VARCHAR}, #{subsectionTime,jdbcType=TIME}, 
  #{subsectionChecked,jdbcType=BIT}, #{section.sectionId,jdbcType=INTEGER}, #{subsectionContent,jdbcType=LONGVARCHAR}
  )
